const Web3 = require('web3');
const provider = new Web3.providers.HttpProvider("http://172.13.0.2:8545");
const web3 = new Web3(provider);

// abi and bytecode generated from simplestorage.sol:
// > solcjs --bin --abi simplestorage.sol
var abi = [{"constant":true,"inputs":[],"name":"storedData","outputs":[{"name":"","type":"uint256"}],"payable":false,"type":"function"},{"constant":false,"inputs":[{"name":"x","type":"uint256"}],"name":"set","outputs":[],"payable":false,"type":"function"},{"constant":true,"inputs":[],"name":"get","outputs":[{"name":"retVal","type":"uint256"}],"payable":false,"type":"function"},{"inputs":[{"name":"initVal","type":"uint256"}],"payable":false,"type":"constructor"}];
var bytecode = "";
var simpleContract = new web3.eth.Contract(abi);


deploy = async () => {

  let accounts = await web3.eth.getAccounts();
  console.log(accounts);

  const data = simpleContract.deploy({ data: bytecode, arguments: [42] }).encodeABI();
  web3.eth.sendTransaction({
    from: accounts[0],
    gas: 0x47b760,
    data: data // deploying a contracrt
  }, function(error, hash){
    console.log(hash);
  });

};
deploy();

